const { chromium } = require('playwright-extra');
const StealthPlugin = require('puppeteer-extra-plugin-stealth');

// Add stealth plugin to avoid bot detection
chromium.use(StealthPlugin());

// Use hardcoded path - consistent in our Docker setup
// Alternatively use $env.PLAYWRIGHT_CHROMIUM_EXECUTABLE_PATH if N8N_BLOCK_ENV_ACCESS_IN_NODE=false
const browser = await chromium.launch({
  executablePath: '/usr/bin/chromium-browser',
  headless: true,
  args: [
    '--no-sandbox',
    '--disable-setuid-sandbox',
    '--disable-dev-shm-usage',
    '--disable-gpu'
  ]
});

const page = await browser.newPage();

// Test against a bot detection site
await page.goto('https://bot.sannysoft.com', { waitUntil: 'networkidle' });

// Wait for tests to complete
await new Promise(resolve => setTimeout(resolve, 3000));

const title = await page.title();
const screenshot = await page.screenshot({ fullPage: true });

await browser.close();

return [{
  json: {
    title,
    url: 'https://bot.sannysoft.com',
    note: 'Check the screenshot - green results mean stealth is working'
  },
  binary: {
    screenshot: {
      data: screenshot.toString('base64'),
      mimeType: 'image/png',
      fileName: 'bot-detection-test.png'
    }
  }
}];
